import React from "react";
import "./shop.styles.scss";
import CollectionsOverview from "../../components/collections-overview/collections-overview.component";
import { firestore, collectionMap } from "../../firebase/firebase.util";

class ShopPage extends React.Component {
  componentDidMount() {
    const collectionRef = firestore.collection("collections");
    collectionRef.onSnapshot((snapshot) => {
      console.log(snapshot.docs[4].data());
      //console.log(collectionMap()
    });
  }
  render() {
    //kad se fetchuje komponenta pozove firestore onstatechange, dobijem snapshot, pozovem funckiju za data normalization
    // iz arr u objekat, i setujem to kao payload i smestim u redux
    //

    return (
      <div className="shop-page">
        <CollectionsOverview />
      </div>
    );
  }
}

export default ShopPage;
