import React from "react";
import "./shop.styles.scss";
import CollectionsOverview from "../../components/collections-overview/collections-overview.component";
import { firestore, collectionMap } from "../../firebase/firebase.util";
import { connect } from "react-redux";
import { fetchShopData } from "../../redux/shop/shop.actions";

class ShopPage extends React.Component {
  unsubscribe = null;
  componentDidMount() {
    const { dispatch } = this.props;
    const collectionRef = firestore.collection("collections");
    this.unsubscribe = collectionRef.onSnapshot(async (snapshot) => {
      dispatch(fetchShopData(collectionMap(snapshot))); // console.log("collectionMap(arr) :>> ", collectionMap(snapshot));
    });
  }

  componentWillUnmount() {
    this.unsubscribe();
  }
  render() {
    return (
      <div className="shop-page">
        <CollectionsOverview />
      </div>
    );
  }
}

export default connect(null)(ShopPage);
